use std::collections::HashSet;

fn main() {
    let line: Vec<char> = "".chars().collect();
    let mut s = HashSet::new();
    let mut start = 0;

    loop {
        for i in 0..4 {
            s.insert(line[start + i].clone());
        }

        if s.len() == 4 {
            println!("found {}, {:?}", start + 4, s);
            break;
        }

        start += 1;
        s.clear();

        if start + 4 >= line.len() {
            println!("not found");
            break;
        }
    }
}
