use std::collections::HashSet;

fn main() {
    let line: Vec<char> = "".chars().collect();
    let size = 14;

    // copied from: https://github.com/Quillbert/AdventOfCode/blob/master/2022/day06b/src/main.rs
    for start in 0..line.len()-size {
        let s: HashSet<_> = line[start..start+size].iter().collect();

        if s.len() == size {
            println!("found {}, {:?}", start + size, s);
            break;
        }
    }
}
